export default {
  name: 'textarea-vue',
  props: {
    placeholder: {
      type: String,
      default: ''
    },
    autofocus: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    fontSize: {
      type: String,
      default: '16px'
    },
    lineHeight: {
      type: String,
      default: '18px'
    },
    border: {
      type: Boolean,
      default: true
    },
    minHeight: {
      type: String,
      default: '180px'
    },
    title: {
      type: String,
      default: ''
    },
    value: {
      type: [String, Number],
      default: ''
    }
  },
  computed: {
    textareaListeners() {
      let vm = this
      return Object.assign({}, this.$listeners, {
          input(event) {
              vm.$emit('input', event.target.value, event)
          },
          blur(event) {
              vm.$emit('blur', event.target.value, event)
          },
          focus(event) {
              vm.$emit('focus', event)
          }
      })
    }
  },
  watch: {
  }
}
